import React from 'react';
import {connect} from 'react-redux';

import {bookActions} from '../_actions';

class BookOKPage extends React.Component {
    componentDidMount() {
        this.props.dispatch(bookActions.getAll());
    }

    constructor(props) {
        super(props);

        this.state = {
            submitted: false
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleClick = this.handleClick.bind(this);
    }


    handleChange(event) {
        const {name, value} = event.target;
        const {user} = this.state;
        this.setState({
            user: {
                ...user,
                [name]: value
            }
        });
    }


    handleClick(e) {
        e.preventDefault();
        this.props.history.push('/checkin');
    }

    render() {
        const {registering, bookings} = this.props;

        if (bookings.loading) {
            return <img
                src="data:image/gif;base64,"/>
        }

        const bookingConfirmed = bookings && bookings.items && bookings.items[0] || {};

        return (

            <div className="col-md-6 col-md-offset-3">
                    <div className="klm-header-container">
                        <h1 className="klm-header">Congratulations </h1>
                        <h2>Your booking is confirmed.</h2>
                        <h3>Please proceed to check-in.</h3>
                    </div>
                    <div className="main-info">
                        <div className="left-side-info-container">
                            <p className="info-left">From: {bookingConfirmed.from}</p>
                            <p className="info-left">To: {bookingConfirmed.to}</p>
                            <p className="info-left">Date: {bookingConfirmed.date && bookingConfirmed.date.toString()}</p>
                            <p className="info-left">Travellers: {bookingConfirmed.travellers}</p>
                        </div>
                    </div>

                    <div className="form-group">
                        <button onClick={this.handleClick} className="btn btn-primary">Check-In now</button>
                        {registering &&
                        <img
                            src="data:image/gif;base64,"/>
                        }
                    </div>
            </div>
        );
    }
}

function mapStateToProps(state) {
    const {bookings, registration} = state;

    const {registering} = registration;
    return {
        bookings,
        registering
    };
}

const connectedBookPage = connect(mapStateToProps)(BookOKPage);
export {connectedBookPage as BookOKPage};